mapper.select:
/* oracle.Code.getCodeListCnt */ SELECT COUNT(*) AS CNT FROM( SELECT ROW_NUMBER() OVER( ORDER BY master_sort ASC) RNUM, Z.* FROM ( SELECT A.CODE AS master_code, A.CODE_NM AS master_name, A.SORT_NO AS master_sort, A.USE_YN AS master_use, A.NOTE AS master_note FROM COM_CODE_MASTER A WHERE 1=1
AND A.CODE = '${selectCodeMaster}'
AND A.USE_YN = '${selectCodeMasterYN}'
) Z )GRID

/* oracle.Code.getCodeList */ SELECT GRID.* FROM( SELECT ROW_NUMBER() OVER( ORDER BY master_sort ASC) RNUM, Z.* FROM ( SELECT A.CODE AS master_code, A.CODE_NM AS master_name, A.SORT_NO AS master_sort, A.USE_YN AS master_use, A.NOTE AS master_note FROM COM_CODE_MASTER A WHERE 1=1
AND A.CODE = '${selectCodeMaster}'
AND A.USE_YN = '${selectCodeMasterYN}'
) Z )GRID WHERE 1 = 1 AND GRID.RNUM BETWEEN #{start} AND #{end}

/* oracle.Code.selectgetCodeListCnt */ SELECT count(*) as CNT FROM( SELECT ROW_NUMBER() OVER( ORDER BY detail_sort asc) RNUM, Z.* FROM ( SELECT A.CODE as master_code, A.CODE_VALUE as detail_code, A.CODE_NM as detail_name, A.NOTE as detail_note, A.USE_YN as detail_use, A.SORT_NO as detail_sort, B.CODE_NM as master_name, B.USE_YN as master_use, B.SORT_NO as master_sort FROM COM_CODE_DETAIL A LEFT OUTER JOIN COM_CODE_MASTER B ON A.CODE = B.CODE WHERE 1=1
and A.CODE = #{select_detail_code}
) Z )GRID

/* oracle.Code.selectgetCodeList */ SELECT GRID.* FROM( SELECT ROW_NUMBER() OVER( ORDER BY detail_sort asc) RNUM, Z.* FROM ( SELECT A.CODE as master_code, A.CODE_VALUE as detail_code, A.CODE_NM as detail_name, A.NOTE as detail_note, A.USE_YN as detail_use, A.SORT_NO as detail_sort, B.CODE_NM as master_name, B.USE_YN as master_use, B.SORT_NO as master_sort FROM COM_CODE_DETAIL A LEFT OUTER JOIN COM_CODE_MASTER B ON A.CODE = B.CODE WHERE 1=1
and A.CODE = #{select_detail_code}
) Z )GRID WHERE 1 = 1 AND GRID.RNUM BETWEEN #{start} AND #{end}

/* oracle.Code.getCodeDetailList */ SELECT B.CODE_VALUE AS VALUE, B.CODE_NM AS NAME FROM COM_CODE_MASTER A INNER JOIN COM_CODE_DETAIL B ON A.CODE = B.CODE WHERE A.USE_YN = 'Y'
AND A.CODE = '${master_code}'
ORDER BY B.SORT_NO

mapper.update:
/* oracle.Code.setCodeDetailMerge */
MERGE INTO COM_CODE_DETAIL A
USING (
    SELECT  #{m_d_master_code} AS CODE
          , #{m_detail_code} AS CODE_VALUE
    FROM DUAL
) B
ON (
		A.CODE = B.CODE
	AND A.CODE_VALUE = B.CODE_VALUE
)
WHEN NOT MATCHED THEN
	INSERT (
		  CODE
		, CODE_VALUE
		, CODE_NM
		, NOTE
		, USE_YN
		, SORT_NO
		, REG_ID
		, REG_DT
		, MOD_ID
		, MOD_DT
	) VALUES (
		  #{m_d_master_code}
		, #{m_detail_code}
		, #{m_detail_name}
		, #{m_detail_note}
		, #{m_detail_use}
		, #{m_detail_sort}
		, #{s_emp_code}
		, SYSTIMESTAMP
		, #{s_emp_code}
		, SYSTIMESTAMP
             )
WHEN MATCHED THEN
	UPDATE SET
		  A.CODE_NM  = #{m_detail_name}
		, A.NOTE     = #{m_detail_note}
		, A.USE_YN   = #{m_detail_use}
		, A.MOD_ID   = #{s_emp_code}
		, A.MOD_DT   = SYSTIMESTAMP
		, A.SORT_NO  = #{m_detail_sort}

mapper.delete:
/* oracle.Code.deleteDetailCode */
	DELETE COM_CODE_DETAIL
	WHERE 1=1
	AND CODE       = #{master_code}
	AND CODE_VALUE = #{detail_code}

/* oracle.Code.deleteMasterCode1 */
	DELETE COM_CODE_DETAIL
	WHERE 1=1
	AND CODE = #{master_code}

/* oracle.Code.deleteMasterCode2 */
	DELETE COM_CODE_MASTER
	WHERE 1=1
	AND CODE = #{master_code}

mapper.select:
/* oracle.Code.select_code_list */
	SELECT KEY, VALUE FROM TBL_CODE_LIST
	WHERE TYPE = #{type}
		AND TYPE_LEVEL = #{type_level}
		AND UPPER_KEY = #{upper_key}
		AND UPPER_VALUE = #{upper_val}
	ORDER BY ORDER_NO ASC
